css:
body {
    font-family: Arial, sans-serif;
    display: flex;
    justify-content: center;
    align-items: center;
    /*height: 100vh;*/
    margin: 0;
    background-color: #e0e0e0;
}

.id-card {
    width: 420px;
    height: 280px;
    border-radius: 10px;
    overflow: hidden;
    box-shadow: 0 0 15px rgba(0, 0, 0, 0.3);
    background-color: #fff;
}

.header {
    display: flex;
    background-color: #0046ad;
    padding: 10px;
    color: #fff;
}

.header .logo {
    width: 50px;
    height: 50px;
    margin-right: 10px;
}

.header .school-info {
    text-align: center;
    flex-grow: 1;
    color:white;
}

.header .school-info h1 {
    font-size: 12px;
    margin: 0;
}

.header .school-info h2 {
    font-size: 14px;
    margin: 0;
}

.header .school-info p {
    font-size: 10px;
    margin: 0;
    color:white;
}

.content {
    display: flex;
    padding: 10px;
}

.content .photo {
    width: 80px;
    height: 100px;
    margin-right: 10px;
}

.content .photo img {
    width: 200%;
    height: 100%;
    object-fit: cover;
    margin-top:26px;
    margin-left:20px;
}

.content .student-info {
    flex-grow: 1;
}

.content .student-info h3 {
    font-size: 14px;
    margin: 0 0 10px;
    text-align: center;
}

 .student-info {
        width: 100%;
    }
    .info-row {
        display: flex;
        margin-bottom: 1px; /* Mengatur jarak antar baris */
        font-size:10px;
        margin-left:35px;
    }
    .info-label {
        flex: 0 0 100px; /* Lebar tetap untuk label */
        font-weight: bold;
        font-size:10px;
    }
    .info-colon {
        flex: 0 0 10px; /* Lebar tetap untuk titik dua */
    }
    .info-value {
        flex: 1; /* Mengisi sisa ruang yang tersedia */
    }

.footer {
    /*text-align: center;*/
    /*padding: 5px;*/
    font-size: 10px;
    margin-top:-20px;
    background-color: #0046ad;
    height:40px;
}
.footer p{
    color:white;
    margin-left:10px;
    margin-top:40px;
}

.footer .signature {
    text-align: right;
    margin-top: 10px;
}

.footer .signature p {
    margin: 0;
}

.footer .signature .signature-image {
    width: 50px;
    height: 20px;
}
