css:
body {
    font-family: Arial, sans-serif;
    /*background-color: #f4f4f4;*/
    /*color: #333;*/
}

.container {
    width: 80%;
    margin: 0 auto;
    /*margin-top:200px;*/
    background-color: #fff;
    /*padding: 20px;*/
    /*box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);*/
}

h2, h3 {
    margin-bottom: 20px;
    text-align: center;
    /*color: #4CAF50;*/
}

h3 {
    /*border-bottom: 2px solid ;*/
    padding-bottom: 5px;
}

table {
    width: 100%;
    border-collapse: collapse;
    margin-top: 20px;
    
}

table, th, td {
    border: 1px solid #ddd;
}

th, td {
    padding: 0px;
    text-align: left;
}

thead {
    /*background-color: #4CAF50;*/
     background-color: #f2f2f2;
    /*color: white;*/
}

tbody tr:nth-child(even) {
    background-color: #f9f9f9;
}

.header-row td {
     background-color: #f2f2f2;
    /*background-color: #4CAF50;*/
    /*color: white;*/
    font-weight: bold;
}

.data-col {
    /*background-color: #e7f3fe;*/
    color: #007bff;
}

.hasil-col {
    /*background-color: #fef7e7;*/
    color: #ff7b00;
}

.detail {
    padding: 20px;
    /*background-color: #f9f9f9;*/
    margin-top: 20px;
    box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
}

.detail p {
    margin: 10px 0;
}

.card {
    padding: 20px;
    background-color: #fff;
    border: 1px solid #ddd;
    box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
    margin-top: 20px;
}

.card h4 {
    margin-bottom: 15px;
    /*color: #4CAF50;*/
}

.card ul {
    list-style-type: none;
    padding: 0;
}

.card ul li {
    background-color: #f9f9f9;
    margin: 5px 0;
    padding: 10px;
    border: 1px solid #ddd;
}
.header-1 {
    text-align: center;
    margin-bottom: 20px;
    border-bottom: 2px solid black;
    display: flex;
    align-items: center;
}
.header-image-1 {
    flex: 1;
}
.header-text {
    flex: 3;
    /*margin-left:-100px;*/
}
.header-image-1 img {
    max-width: 100%;
    height: auto;
    display: block;
}

.header-1 h1, .header-1 h2, .header-1 h3 {
    margin: 5px 0;
}

.header-1 p {
    margin: 10px 0;
    font-size: 0.9em;
}

.id-card {
    width: 360px;
    height: 230px;
    border-radius: 10px;
    overflow: hidden;
    box-shadow: 0 0 15px rgba(0, 0, 0, 0.3);
    background-color: #fff;
    border:2px solid black;
}

.header {
    display: flex;
    background-color: #0046ad;
    padding: 10px;
    color: white;
}

.header .logo {
    width: 50px;
    height: 50px;
    margin-right: 10px;
}

.header .school-info {
    text-align: center;
    flex-grow: 1;
}

.header .school-info h1 {
    font-size: 12px;
    margin: 0;
}

.header .school-info h2 {
    font-size: 14px;
    margin: 0;
}

.header .school-info p {
    font-size: 10px;
    margin: 0;
}

.content {
    display: flex;
    padding: 10px;
}

.content .photo {
    width: 80px;
    height: 100px;
    margin-right: 10px;
}

.content .photo img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    margin-top:20px;
}

.content .student-info {
    flex-grow: 1;
}

.content .student-info h4 {
    font-size: 12px;
    margin: 0 0 10px;
    text-align: center;
    border-bottom: 2px solid ;
}

.content .student-info p {
    font-size: 10px;
}


.footer {
    text-align: center;
    padding: 5px;
    font-size: 10px;
}

.footer .signature {
    text-align: right;
    margin-top: 10px;
}

.footer .signature p {
    margin: 0;
}

.footer .signature .signature-image {
    width: 50px;
    height: 20px;
}
