css:
body {
    font-family: Arial, sans-serif;
    margin: 0;
    padding: 0;
    background-color: #f4f4f4;
}

.container {
    width: 100%;
    margin: 0 auto;
    padding: 10px;
    background-color: white;
}

.title {
    text-align: center;
    margin-bottom: 25px;
}

.title h2 {
    margin: 0;
    color: #333;
}

.info {
    margin-bottom: 20px;
}

.info p {
    font-size: 16px;
    line-height: 14px;
}

.info .label {
    display: inline-block;
    width: 120px; /* Adjust the width as necessary */
    font-weight: bold;
    color: #333;
    text-align: left;
}

table {
    width: 100%;
    border-collapse: collapse;
    margin-bottom: 20px;
}

table, th, td {
    border: 1px solid #ddd;
}

th, td {
    padding: 10px;
    text-align: left;
}

th {
    background-color: #f4f4f4;
    font-weight: bold;
}

.jumlah, .jumlah_data {
    text-align: center;
}

tbody tr:nth-child(even) {
    background-color: #f9f9f9;
}

tbody tr:hover {
    background-color: #f1f1f1;
}

/* Add print-specific styles */
@media print {
    .info p, .info .label {
        border: none !important;
    }
}
